    ScenePage.prototype.CreateActorLine = function (line, ids, lineCount) {
        var y = -line * 200 + (lineCount * 200) / 2;
        var x = 0;
        var xSpace = 150;
        for (var i = 0; i < ids.length; i++) {
            var actor = ActorBase.CreateActor(ids[i]);
            actor.SetScale(0.6, 0.6);
            actor.Play();
            actor.SetPos(x, y - dMath.Sin(i / 10.0) * 100);
            if (ids.length % 2 == 0)
                actor.positionX -= xSpace / 2;
            if (x <= 0)
                x = (-x) + xSpace;
            else
                x = -x;
            this.ActorLayer.AddChild(actor, 0);
            actor.m_pNameLabel.SetPos(actor.positionX, actor.positionY);
            this.ActorLayer.AddChild(actor.m_pNameLabel);
            actor.m_pNameLabel.SetScale(actor.scaleX, actor.scaleY);
            this.m_vecActors.push(actor);
        }
    };
